          <p>{events.eventFreeOrPay}</p>
          <p>{events.eventPlace}</p>
          <p>{events.eventDescription}</p>
          <p>{events.eventAuthorEmail}</p>
          <p>{events.eventAuthorPhone}</p>
          <Link to={'/allEvents'}>Go back</Link>
    </form>

